generated.css:
*{
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}
ul { list-style-type:none;}
/* page css */

.box {
    display: flex;
    flex-direction: column;
    width: 100vw;
    height: 100vh;
}


header {
    padding: 10px;
    margin-top: 10px;
    cursor: pointer;
}
header svg {
    margin-left: 10px;
    transition: all 0.5s;
}
header svg:hover {
    filter: invert(78%) sepia(52%) saturate(4753%) hue-rotate(359deg) brightness(102%) contrast(105%);
}
header svg .bigger:hover {
    transform: scale(1.3);
    filter: invert(78%) sepia(52%) saturate(4753%) hue-rotate(359deg) brightness(102%) contrast(105%);
}
header svg.active {
    transform: scale(1.2);
    filter: invert(78%) sepia(52%) saturate(4753%) hue-rotate(359deg) brightness(102%) contrast(105%);
}


.aside ul{
    display: inline-block;
    margin-top: 20px;
    margin-left: 10px;
}
.aside li{
    width: 38px;
    height: 38px;
    border-radius: 50%;
    margin-top: 10px;
    border: 2px solid #ccc;
    transition: all 1s;
}
.aside li:hover{
    cursor: pointer;
}
.aside li.active{
    transform: scale(1.2);
}
